import React, { useState, useEffect } from "react";
import axios from "axios";

const Statistics = () => {
  const [selectedMonth, setSelectedMonth] = useState("");
  const [statsData, setStatsData] = useState({});
  useEffect(() => {
    const fetchData = async (req, res) => {
      try {
        const response = await axios.get(
          `http://localhost:9000/statistic/${selectedMonth}`
        );
        setStatsData(response.data);
      } catch (error) {
        console.error("Error in Fetching Data: ", error);
      }
    };
    fetchData();
  }, [selectedMonth]);

  const handleDropdownChange = (event) => {
    setSelectedMonth(event.target.value);
  };

  return (
    <div>
      <label htmlFor="dropdown">Select Month:</label>
      <select
        id="dropdown"
        value={selectedMonth}
        onChange={handleDropdownChange}
      >
        <option value="">Select...</option>
        <option value="January">January</option>
        <option value="February">February</option>
        <option value="March">March</option>
        <option value="April">April</option>
        <option value="May">May</option>
        <option value="June">June</option>
        <option value="July">July</option>
        <option value="August">August</option>
        <option value="September">September</option>
        <option value="October">October</option>
        <option value="November">November</option>
        <option value="December">December</option>
      </select>
      {selectedMonth && <p>You selected: {selectedMonth}</p>}
      {/* Render statsData properties */}
      <p>Data count: {statsData.count}</p>
      <p>Total Sale Amount: {statsData.totalSaleAmount}</p>
      <p>Total Sold Items: {statsData.totalSoldItems}</p>
      <p>Total Not Sold Items: {statsData.totalNotSoldItems}</p>

      {/* Rendering priceRanges */}
      <p>Price Ranges:</p>
      <ul>
        {Object.entries(statsData.priceRanges || {}).map(([range, count]) => (
          <li key={range}>
            {range}: {count}
          </li>
        ))}
      </ul>

      {/* Rendering categoryCounts */}
      <p>Category Counts:</p>
      <ul>
        {Object.entries(statsData.categoryCounts || {}).map(
          ([category, count]) => (
            <li key={category}>
              {category}: {count}
            </li>
          )
        )}
      </ul>

      {/* Rendering data array */}
      <p>Data:</p>
      <ul>
        {statsData.data &&
          statsData.data.map((item) => (
            <li key={item.id}>
              {item.category} - {item.price} - {item.sold ? "Sold" : "Not Sold"}
            </li>
          ))}
      </ul>
    </div>
  );
};

export default Statistics;
