import React, { useState, useEffect, useCallback } from "react";
import ProductCard from "./ProductCard";
import axios from "axios";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedMonth, setSelectedMonth] = useState("March");
  const [searchValue, setSearchValue] = useState("");

  const fetchData = useCallback(async () => {
    try {
      const response = await axios.post(
        `http://localhost:9000/search/${selectedMonth}`,
        {
          searchValue: searchValue,
        }
      );
      setProducts(response.data);
    } catch (error) {
      console.error("Error in Fetching Data: ", error);
    }
  }, [selectedMonth, searchValue]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const pageSize = 3;
  const totalCards = products.length;
  const totalPages = Math.ceil(totalCards / pageSize);

  // Ensure currentPage is within the valid range
  const validPage = Math.min(Math.max(1, currentPage), totalPages);

  const startIndex = (validPage - 1) * pageSize;
  const endIndex = startIndex + pageSize;
  const currentProducts = products.slice(startIndex, endIndex);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleSearch = () => {
    setCurrentPage(1); // Reset to the first page when performing a new search
    fetchData();
  };

  const handleClearSearch = () => {
    setSearchValue("");
    setCurrentPage(1); // Reset to the first page when clearing the search
  };
  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages));
  };

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };
  const handleCardClick = (product) => {
    console.log("Card clicked:", product);
    setSelectedProduct(product);
    setIsPopupVisible(true);
  };

  const closePopup = () => {
    setIsPopupVisible(false);
  };
  return (
    <div>
      <div className="search-container">
        <label>Select Month:</label>
        <select
          onChange={(e) => setSelectedMonth(e.target.value)}
          value={selectedMonth}
        >
          {Array.from({ length: 12 }).map((_, index) => (
            <option key={index} value={getMonthName(index)}>
              {getMonthName(index)}
            </option>
          ))}
        </select>
        <label>Search:</label>
        <input type="text" value={searchValue} onChange={handleSearchChange} />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleClearSearch}>Clear Search</button>
      </div>
      <div className="card-container">
        {currentProducts.map((product) => (
          <ProductCard
            key={product.id}
            title={product.title}
            price={product.price}
            imageSrc={product.image}
            onClick={() => handleCardClick(product)}
          />
        ))}
      </div>
      <div className="pagination">
        <button onClick={handlePrevPage}>&lt; Previous</button>
        <p>
          Page {validPage} of {totalPages}
        </p>
        <button onClick={handleNextPage}>Next &gt;</button>
      </div>
      {isPopupVisible && (
        <ProductPopup product={selectedProduct} onClose={closePopup} />
      )}
    </div>
  );
};

const getMonthName = (index) => {
  const monthNames = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];
  return monthNames[index];
};

export default ProductList;
