// ProductCard.jsx
import React from "react";

const ProductCard = (props) => {
  const { title, price, imageSrc } = props;

  return (
    <div className="card">
      <div className="card-img">
        {imageSrc && <img src={imageSrc} alt={title} />}
      </div>
      <div className="card-body">
        <p>{title}</p>
        <p>{price} $</p>
      </div>
    </div>
  );
};

export default ProductCard;
