import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const PriceRangeBarChart = ({ priceRanges }) => {
  const chartRef = useRef();

  useEffect(() => {
    if (!priceRanges || Object.keys(priceRanges).length === 0) {
      return;
    }

    const svg = d3.select(chartRef.current);

    const data = Object.entries(priceRanges).map(([range, count]) => ({
      range,
      count,
    }));

    const margin = { top: 20, right: 30, bottom: 30, left: 40 };
    const width = 800 - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    const x = d3
      .scaleBand()
      .domain(data.map((d) => d.range))
      .range([0, width])
      .padding(0.1);

    const y = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => d.count)])
      .range([height, 0]);

    svg.selectAll("*").remove();

    svg
      .append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`)
      .selectAll("rect")
      .data(data)
      .enter()
      .append("rect")
      .attr("x", (d) => x(d.range))
      .attr("y", (d) => y(d.count))
      .attr("width", x.bandwidth())
      .attr("height", (d) => height - y(d.count));

    svg
      .append("g")
      .attr("transform", `translate(${margin.left}, 0)`)
      .call(d3.axisLeft(y));

    svg
      .append("g")
      .attr("transform", `translate(${margin.left}, ${height})`)
      .call(d3.axisBottom(x));
  }, [priceRanges]);

  return <svg ref={chartRef} width={width} height={height}></svg>;
};

export default PriceRangeBarChart;
