import React from "react";

const ProductCard = (props) => {
  const { title, description, imageSrc } = props;

  return (
    <div className="card">
      {imageSrc && <img src={imageSrc} alt={title} />}
      <div className="card-body">
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    </div>
  );
};

export default ProductCard;
